import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './header';
import Posts from './posts';
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import SinglePost from './singlepost'; // Importa el componente SinglePost

function Perfil() {
    const [user, setUser] = useState(null);
    const [selectedPost, setSelectedPost] = useState(null); // Nuevo estado para almacenar el post seleccionado

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const sesionUsuario = localStorage.getItem('sesion');
                const nombreUsuario = sesionUsuario ? JSON.parse(sesionUsuario) : null;
                const response = await axios.get(`http://localhost:3001/obtener-informacion-usuario/${nombreUsuario.usuario}`);
                setUser(response.data);
            } catch (error) {
                console.error('Error al obtener la información del usuario:', error);
            }
        };

        fetchUserData();
    }, []);

    const getImagePath = (imageUrl) => {
        if (imageUrl && imageUrl.startsWith('http://localhost:3001/img/')) {
            return imageUrl.substring(imageUrl.lastIndexOf('/') + 1);
        }

        return imageUrl;
    };

    const handlePostClick = (post) => {
        setSelectedPost(post); // Cuando se hace clic en un post, establece el post seleccionado
    };

    const handleCloseSinglePost = () => {
        setSelectedPost(null); // Cuando se cierra el post único, establece el post seleccionado como null
    };

    return (
        <div>
            <Header />
            <div className="container">
                <div className='px-5'>
                    <div className="row px-5">
                            {user && (
                                <div className='col-12'style={{ backgroundColor: '#CA8682' }}>
                                    <div className='row'>
                                    <h2>Información del Usuario</h2>
                                        <div className="col-lg-6 col-md-4 col-sm-12 text-center">
                                                <div className="form-group">
                                                    <div className="img-container">
                                                        <img src={`/img/${getImagePath(user.foto_Users)}`} alt="Foto de perfil" 
                                                        style={{ width: '60%', height: '300px', objectFit: 'cover', borderRadius:'60px'}}/>
                                                    </div>
                                                </div>
                                        </div>
                                        <div className='col-lg-6 col-md-8 col-sm-12'>
                                            <div className="form-group">
                                                <label>Nombre:</label>
                                                <input type="text" className="form-control bg-transparent" value={user.nombres_Users} readOnly />
                                            </div>
                                            <div className="form-group">
                                                <label>Correo:</label>
                                                <input type="email" className="form-control bg-transparent" value={user.correoUser} readOnly />
                                            </div>
                                            <div className="form-group">
                                                <label>Edad:</label>
                                                <input type="text" className="form-control bg-transparent" value={user.edad_Users} readOnly />
                                            </div>
                                            <div className="form-group">
                                                <label>Descripción:</label>
                                                <textarea className="form-control bg-transparent" value={user.descripcion_Users} readOnly />
                                            </div>
                                            <div className="form-group">
                                                <label>Área:</label>
                                                <input type="text" className="form-control bg-transparent border-0" value={user.area_Users} readOnly />
                                            </div>
                                        </div>
                                    </div>
 

                                </div>

                            )}

                    </div>
                </div>
                <div className='row'>
                    <div className="col-12">
                            <Posts onPostClick={handlePostClick} />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Perfil;
